mapper.select:
SELECT COUNT(*)
FROM member
WHERE user_id = #{userId}

SELECT user_id AS userId,
user_password AS userPassword
FROM
member
WHERE user_id = #{userId}
AND user_password = #{userPassword}

mapper.update:
UPDATE member SET
user_password= #{userPassword}
WHERE
user_id = #{userId}

mapper.select:
select
filename from profile_attach where user_id =
#{userId}
